import Constants from '../constants';
const initialState = {
    apiData: [],
    providerList: [],
    universitiesList: [],
    subjectList: [],
    childSubjectList: [],
    isLoading: true,
    error: null,
    filteredArray: [],
    totalCourseFound : 0,
    userCoursesFound: 0
};

function CourseListReducer(state = initialState, action) {
    switch (action.type) {
    case Constants.SET_API_DATA:
        return {
            ...state,
            apiData :  action.payload,
        };
    case Constants.SET_PROVIDER_LIST_DATA:
        return {
            ...state,
            providerList :  action.payload,
        };
    case Constants.SET_PROVIDER_LIST_DATA:
        return {
            ...state,
            providerList :  action.payload,
        };
    case Constants.SET_PROVIDER_LIST_DATA:
        return {
            ...state,
            providerList :  action.payload,
        };
    case Constants.SET_PROVIDER_LIST_DATA:
        return {
            ...state,
            providerList :  action.payload,
        };
    default:
        return state;
    }
}

export default CourseListReducer;

